    Payload.Size = "946 KB"
    Payload.FullSize = "1.1 MB"
    Payload.MidSize = "326 KB"
    Payload.UPX_Size = "262 KB"
    Payload.Score = 8
    Payload.SourceCode = METERPRETER_TCP

    ClearScreen()
